import React, { useState, useEffect } from 'react'
import { Link, useParams } from 'react-router-dom'
/* Imported useParams to replace "params" and "match" */
import { Row, Col, Image, ListGroup, Card, Button } from 'react-bootstrap'
import Rating from '../components/Rating'
import axios from 'axios'

const ProductScreen = () => {
// const ProductScreen = ({ match }) => {
// const product = products.find((p) => p._id === match.params.id)}

// Version 6 of react-router-dom no longer uses the match prop
// Use the following link if you have this problem again with react-router-dom version 5 incompatibility
// https://stackoverflow.com/questions/70290770/react-typeerror-cannot-read-properties-of-undefined-reading-params

  // const { id } = useParams();
  
  // const product = products.find((p) => String(p._id) === id)

  const [product, setProduct] = useState({})

  useEffect(() => {
    const fetchProduct = async () => {
      const { id } = useParams();

      const { data } = await axios.get(`/api/products/${id}`)

      setProduct(data)
    }

    fetchProduct()
  }, [])

  return (
  <>
    <Link className='btn btn-light my-3' to='/'>
      Go Back
    </Link>

    <Row>
      <Col md={6}>
        <Image src={product.image} alt={product.name} fluid />
      </Col>

      <Col md={3}>
        <ListGroup variant='flush'>
          <ListGroup.Item>
            <h3>{product.name}</h3>
          </ListGroup.Item>

          <ListGroup.Item>
            <Rating 
              value={product.rating} 
              text={`${product.numReviews} reviews`} 
            />
          </ListGroup.Item>

          <ListGroup.Item>
            Price: ${product.price}
          </ListGroup.Item>

          <ListGroup.Item>
            Description: {product.description}
          </ListGroup.Item>
        </ListGroup>
      </Col>

      <Col md={3}>
        <Card>
          <ListGroup variant='flush'>
            <ListGroup.Item>
              <Row>
                <Col>
                  Price: 
                </Col>

                <Col>
                  <strong>${product.price}</strong>
                </Col>
              </Row>
            </ListGroup.Item>

            <ListGroup.Item>
              <Row>
                <Col>
                  Status: 
                </Col>

                <Col>
                  {product.countInStock > 0 ? 'In Stock' : 'Out Of Stock'}
                </Col>
              </Row>
            </ListGroup.Item>

            <ListGroup.Item>
              <Button className='btn-block' type='button' disabled={product.countInStock === 0}>
                Add To Cart
              </Button>
            </ListGroup.Item>
          </ListGroup>
        </Card>
      </Col>
    </Row>
  </>
  )
}

export default ProductScreen
