import React, { useState, useEffect } from 'react'
import { useLocation, useNavigate } from 'react-router-dom'
import { Form, Button, Row, Col } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import Message from '../components/Message'
import Loader from '../components/Loader'
import { getUserDetails, updateUserProfile } from '../actions/userActions'

const ProfileScreen = () => {
  const [name, setName] = useState('')
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const [confirmPassword, setConfirmPassword] = useState('')
  const [message, setMessage] = useState(null)

  const dispatch = useDispatch()

  const userDetails = useSelector(state => state.userDetails)

  const { loading, error, user } = userDetails

  const userLogin = useSelector(state => state.userLogin)

  const { userInfo } = userLogin

  const userUpdateProfile = useSelector(state => state.userUpdateProfile)

  const { success } = userUpdateProfile

  // const location = useLocation()

  const history = useNavigate();

  useEffect(() => {
    if (!userInfo) {
        history('/login')
    } else {
        if (!user.name) {
            dispatch(getUserDetails('profile'))
        } else {
            setName(user.name)
            setEmail(user.email)
        }
    }
  }, [dispatch, history, userInfo, user])  

  const submitHandler = (e) => {
    e.preventDefault()
    
    if (password !== confirmPassword) {
        setMessage('Passwords do not match')
    } else {
        dispatch(updateUserProfile({ id: user._id, name, email, password }))
    }
  }

  return (
    <Row>
      <Col md={3}>
        <h2>User Profile</h2>

        {message && <Message variant='danger'>{message}</Message>}

        {error && <Message variant='danger'>{error}</Message>}

        {error && <Message variant='success'>Profile Updates</Message>}

        {loading && <Loader />}

        <Form onSubmit={submitHandler}>
          <Form.Group controlId='name'>
            <Form.Label>Name</Form.Label>

            <Form.Control 
              type='name' 
              placeholder='Enter name' 
              value={name}
              onChange={(e) => setName(e.target.value)}
            ></Form.Control>
          </Form.Group>

          <Form.Group controlId='email'>
            <Form.Label className='margin-top-1point5rem'>Email Address</Form.Label>

            <Form.Control 
              type='email' 
              placeholder='Enter email' 
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            ></Form.Control>
          </Form.Group>

          <Form.Group controlId='password'>
            <Form.Label className='margin-top-1point5rem'>Password</Form.Label>

            <Form.Control 
              type='password' 
              placeholder='Enter password' 
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            ></Form.Control>
          </Form.Group>

          <Form.Group controlId='confirmPassword'>
            <Form.Label className='margin-top-1point5rem'>Confirm password</Form.Label>

            <Form.Control 
              type='password' 
              placeholder='Confirm password' 
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
            ></Form.Control>
          </Form.Group>

          <Button type='submit' variant='primary' className='margin-top-1point5rem'>
            Update
          </Button>
        </Form>
      </Col>

      <Col md={9} className='margin-top-1rem'>
        <h2>My Orders</h2>
      </Col>
    </Row>
  )
}

export default ProfileScreen
